import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';


class App extends Component {

  async componentDidMount() {

  const web3 = new Web3(Web3.givenProvider || new Web3.providers.WebsocketProvider('ws://localhost:3001'), null, {});

  const latestBlockNo = await web3.eth.getBlockNumber();

  console.log(latestBlockNo);   

  //const blockData= web3.eth.getBlock(latestBlockNo);

  // console.log(web3.eth.getBlock('latest'));
  // //blockData.parentHash.then(console.log);
  // console.log(JSON.stringify(blockData));
  const block = web3.eth.getBlock('latest');
  const blockData=block.then(function (value){
    return value.hash;
  })
  console.log(JSON.stringify(blockData));

  // block.then(function(result) {
  // var hashValue= block.hash;
  // console.log(hashValue);
  // })
}

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}

export default App;
