import React, { Component,  } from 'react';
import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import Block from "./components/Block";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = { username: "Seda", loggedIn: true, blocks: []};
    this.web3 = new Web3(Web3.givenProvider || new Web3.providers.WebsocketProvider('ws://localhost:3000'), null, {});
  }

  getBlock(blockHash='latest', depth=0){
    if(depth < 10)
    {
      let obj = this
      this.web3.eth.getBlock(blockHash).then(r => {
          console.log('response status: ', r)
          obj.state.blocks.push({number: r.number, hash:r.hash, parentHash: r.parentHash });
          console.log('parent hash :' + r.parentHash);
          this.setState({blocks:obj.state.blocks});
          this.getBlock(r.parentHash, depth+1)
          console.log(obj.state.blocks);
      })
    }
  }

  componentDidMount() {
    this.getBlock();
    const subscription = this.web3.eth.subscribe('pendingTransactions', function(error, result){
      if (!error)
          console.log("ben error degilim "+ error);
      if(result)
          console.log("ben result" + result );
  })
  .on("data", function(transaction){
      console.log(transaction);
  });

  // unsubscribes the subscription
  subscription.unsubscribe(function(error, success){
      if(success)
          console.log('Successfully unsubscribed!');
  });
  }

  render() {
    return (
      <div className="App">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">Welcome to {this.state.username} APP</h1>
          <h2 > Latest Blocks</h2>
        <div id= "block" style={{ display: "block" }}>
          {this.state.blocks.map(block => (
            <Block block={block} />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
